Create Procedure [dbo].[spGetComiteTecnicoByID] 
@ID int
AS
BEGIN
    SET NOCOUNT ON;
    select  
   ComiteTecnico.ID ,
ComiteTecnico.Fideicomiso ,
ComiteTecnico.Nombre ,
ComiteTecnico.Integracion ,
ComiteTecnico.Facultades ,
ComiteTecnico.Comentarios ,
ComiteTecnico.FechaConstitucion ,
ComiteTecnico.MiembrosQuorum ,
ComiteTecnico.Estatus ,
ComiteTecnico.NombrePresidentePropietario ,
ComiteTecnico.RfcPresidentePropietario ,
ComiteTecnico.NacionalidadPresidentePropietario ,
ComiteTecnico.PepPresidentePropietario ,
ComiteTecnico.NombrePresidenteSuplente ,
ComiteTecnico.RfcPresidenteSuplente ,
ComiteTecnico.NacionalidadPresidenteSuplente ,
ComiteTecnico.PepPresidenteSuplente ,
ComiteTecnico.NombreSecretarioPropietario ,
ComiteTecnico.RfcSecretarioPropietario ,
ComiteTecnico.NacionalidadSecretarioPropietario ,
ComiteTecnico.PepSecretarioPropietario ,
ComiteTecnico.NombreSecretarioSuplente ,
ComiteTecnico.RfcSecretarioSuplente ,
ComiteTecnico.NacionalidadSecretarioSuplente ,
ComiteTecnico.PepSecretarioSuplente ,
ComiteTecnico.NombreVocalPropietario ,
ComiteTecnico.RfcVocalPropietario ,
ComiteTecnico.NacionalidadVocalPropietario ,
ComiteTecnico.PepVocalPropietario ,
ComiteTecnico.NombreVocalSuplente ,
ComiteTecnico.RfcVocalSuplente ,
ComiteTecnico.NacionalidadVocalSuplente ,
ComiteTecnico.PepVocalSuplente 
         , Fideicomiso1.GeneralesNumero as GeneralesNumero1
    from ComiteTecnico  left join Fideicomiso as Fideicomiso1 on ComiteTecnico.Fideicomiso = Fideicomiso1.ID

    where ComiteTecnico.ID = @ID;

		
END

